// Ensure the script runs after the DOM is fully loaded
$(document).ready(function () {
    console.log("Widget loaded. Waiting for 2 seconds...");

    // Wait for 2 seconds
    setTimeout(function () {
        console.log("2 seconds passed. Attempting submission...");

        try {
            // Attempt submission via JFCustomWidget API
            if (typeof JFCustomWidget !== "undefined" && JFCustomWidget.sendSubmit) {
                JFCustomWidget.sendSubmit({
                    valid: true,
                    value: "Countdown Complete",
                });
                console.log("Submission triggered via JFCustomWidget.");
            } else {
                console.error("JFCustomWidget is not available.");
            }
        } catch (error) {
            console.error("Error during submission:", error);
        }
    }, 2000);
});
